import{_ as s}from"./artschoolbanner.8e981503.mjs";import{_ as d}from"./_plugin-vue_export-helper.7287ed4b.mjs";import{o as e,e as a,f as t,h as i}from"./entry.62d78d4c.mjs";const l=""+new URL("be+home+page.a0b3874c.png",import.meta.url).href,c=""+new URL("competitive+analysis.339dd392.png",import.meta.url).href;const n={},o=t('<link rel="stylesheet" href="https://use.typekit.net/qpz2fcw.css"><div class="card"><div class="card-image"><figure class="image is-3by1"><img src="'+s+'" alt="Placeholder image"></figure></div></div><div class="card" style="padding-top:6rem;padding-left:45vh;padding-right:45vh;"><div class="card-content" id="first-card"><div class="block-double"><div class="project-head-text">Bruin Entrepreneurs: <br> Redesigning the website to streamline the user experience for potential donors and UCLA students </div></div></div></div><div class="columns" style="padding-top:2rem;padding-left:45vh;padding-right:45vh;"><div class="column is-one-third"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> TIMELINE <br></div><p>Ongoing </p></div></div></div></div><div class="column is-one-third"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> TOOLS </div><p>Figma </p></div></div></div></div><div class="column is-one-third"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> ROLE </div><p>UI/UX Designer </p></div></div></div></div></div><div class="columns" style="padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> BRIEF </div><p>I worked to redesign BE\u2019s website to streamline the user experience for potential donors and UCLA students. </p></div></div></div></div></div><div class="card"><div class="card-image"><figure class="image is-3by1"><img src="https://bulma.io/images/placeholders/1280x960.png" alt="Placeholder image"></figure></div></div><div class="columns" style="padding-top:4rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> PROBLEM </div><p>Potential donors and UCLA students have trouble identifying what Bruin Entrepreneurs\u2019 initiatives are when they visit the website. </p></div></div></div></div></div><div class="columns" style="padding-top:3rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> INITIAL DESIGNS</div><p>Bruin Entrepreneurs\u2019 website lacks immediate information and calls to action. </p></div></div></div></div></div><div class="card"><div class="card-image"><figure class="image is-3by1"><img src="'+l+'" alt="original design"></figure></div></div><div class="columns" style="padding-top:5rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> RESEARCH</div><p>I worked with the sponsorships team to interview alumni and current students </p></div><div class="block-double"><div class="subtitle">FINDINGS ABOUT CURRENT STUDENTS</div></div><div class="block-double"><p>1. Students rarely use the website because they don&#39;t find any information about current on campus events</p></div><div class="block-double"><p>2. Students have a hard time navigating on &quot;get involved&quot; page to find updates about club applications every quarter </p></div><div class="block-double"><p>3. Students can\u2019t tell that BE is a business club from the website\u2019s visual design; it is too generic </p></div></div></div></div></div><div class="columns" style="padding-top:5rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle">FINDINGS ABOUT ALUMNI + DONORS</div></div><div class="block-double"><p>1. Donors want to learn about alumni success stories, but none are shown on the website and they have to look through social media.</p></div><div class="block-double"><p>2. Donors can\u2019t find a way to contact BE from the website about making donations </p></div></div></div></div></div><div class="columns" style="padding-top:3rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle"> COMPETITIVE ANALYSIS</div><p>I looked at competitive clubs and VC firm sites to find inspiration </p></div></div></div></div></div><div class="card"><div class="card-image"><figure class="image is-3by1"><img src="'+c+'"></figure></div></div><div class="columns" style="padding-top:5rem;padding-left:45vh;padding-right:45vh;"><div class="column"><div class="card"><div class="card-content"><div class="block-double"><div class="subtitle">WHAT I TOOK AWAY WAS THAT:</div></div><div class="block-double"><p>1. Putting highlights on the home page is both engaging and creates emotional appeal to the user </p></div><div class="block-double"><p>2. Having a left menu helps unify club initiatives and makes navigating through information easier </p></div><div class="block-double"><p>3. Creating individual pages for donors and students separates the two groups and their indivudual goals. Additionally, putting donors first on the navigation bar would put emphasis that BE is a nonprofit.</p></div></div></div></div></div>',14),v=i("div",{class:"columns",style:{"padding-top":"5rem","padding-left":"45vh","padding-right":"45vh"}},[i("div",{class:"column"},[i("div",{class:"card"},[i("div",{class:"card-content"},[i("div",{class:"block-double"},[i("div",{class:"subtitle"},"PROTOTYPE"),i("p",null,"I was able to create small illustrations for the website throughout the summer ")]),i("figure",{class:"image is-16by9"},[i("iframe",{class:"has-ratio",width:"640",height:"360",src:"https://www.youtube.com/embed/YE7VzlLtp-4",frameborder:"0",allowfullscreen:""})])])])])],-1),r=[o,v];function u(p,g){return e(),a("div",null,r)}const f=d(n,[["render",u]]);export{f as default};
